import React, { useState, useEffect } from 'react';
import ArmorTypePage from './ArmorTypePage';
import CaryllRunesPage from './CaryllRunesPage';
import HunterToolsPage from './HunterToolsPage';
import WeaponTypePage from './WeaponTypePage';
import './App.css';

function App() {
  // Define a state to track the current route
  const [currentRoute, setCurrentRoute] = useState(window.location.pathname);

  // Update the current route when the URL changes
  useEffect(() => {
    const handleRouteChange = () => {
      setCurrentRoute(window.location.pathname);
    };

    // Listen for URL changes
    window.addEventListener('popstate', handleRouteChange);

    // Cleanup
    return () => {
      window.removeEventListener('popstate', handleRouteChange);
    };
  }, []);

  // Render the component based on the current route
  const renderComponent = () => {
    switch (currentRoute) {
      case '/armor':
        return <ArmorTypePage />;
      case '/caryll-runes':
        return <CaryllRunesPage />;
      case '/hunter-tools':
        return <HunterToolsPage />;
      case '/weapons':
        return <WeaponTypePage />;
      default:
        // Handle 404 or other routes
        return <div>Page Not Found</div>;
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Bloodborne Wiki</h1>
      </header>
      <div className="content">
        <div className="links">
          {/* Update the URL without causing a page reload */}
          <button onClick={() => window.history.pushState({}, '', '/armor')}>Armor</button>
          <button onClick={() => window.history.pushState({}, '', '/caryll-runes')}>Caryll Runes</button>
          <button onClick={() => window.history.pushState({}, '', '/hunter-tools')}>Hunter Tools</button>
          <button onClick={() => window.history.pushState({}, '', '/weapons')}>Weapons</button>
        </div>
      </div>
      {/* Render the component based on the current route */}
      {renderComponent()}
    </div>
  );
}

export default App;
