import React, { useState, useEffect } from 'react';
import axios from 'axios';

function WeaponTypePage() {
  const [weaponTypes, setWeaponTypes] = useState([]);
  const [selectedWeaponType, setSelectedWeaponType] = useState(null);
  const [weapons, setWeapons] = useState([]);

  useEffect(() => {
    // Fetch weapon types from the backend
    const fetchWeaponTypes = async () => {
      try {
        const response = await axios.get('/api/weaponTypes');
        setWeaponTypes(response.data);
      } catch (error) {
        console.error('Error fetching weapon types:', error);
      }
    };

    fetchWeaponTypes();
  }, []);

  const handleSelectWeaponType = async (weaponType) => {
    setSelectedWeaponType(weaponType);

    try {
      const response = await axios.get(`/api/weaponsByType/${weaponType.weaponTypeID}`);
      setWeapons(response.data);
    } catch (error) {
      console.error('Error fetching weapons by type:', error);
      setWeapons([]);
    }
  };

  return (
    <div>
      <h2>Select Weapon Type</h2>
      <div className="links">
        {/* Render each weapon type as a clickable link */}
        {weaponTypes.map((weaponType) => (
          <a
            key={weaponType.weaponTypeID}
            className={selectedWeaponType === weaponType ? 'active' : ''}
            onClick={() => handleSelectWeaponType(weaponType)}
          >
            {weaponType.weaponTypeName}
          </a>
        ))}
      </div>
      <div>
        <h2>{selectedWeaponType && selectedWeaponType.weaponTypeName}</h2>
        <div className="weapons">
          {weapons.map((weapon) => (
            <div key={weapon.weaponID} className="weapon" style={{ marginTop: '60px' }}>
              <h3>{weapon.weaponName}</h3>
              <img src={`data:image/png;base64,${weapon.filepath}`} alt={weapon.weaponName} />
              <p>Physical Attack: {weapon.physicalAttack}</p>
              <p>Blood Attack: {weapon.bloodAttack}</p>
              <p>Arcane Attack: {weapon.arcaneAttack}</p>
              <p>Fire Attack: {weapon.fireAttack}</p>
              <p>Bolt Attack: {weapon.boltAttack}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default WeaponTypePage;
