import React, { useState, useEffect } from 'react';
import axios from 'axios';

function WeaponSelectionPage({ history }) {
  const [weaponTypes, setWeaponTypes] = useState([]);

  useEffect(() => {
    const fetchWeaponTypes = async () => {
      try {
        const response = await axios.get('/api/weaponTypes');
        setWeaponTypes(response.data);
      } catch (error) {
        console.error('Error fetching weapon types:', error);
      }
    };

    fetchWeaponTypes();
  }, []);

  const handleSelectWeaponType = (weaponType) => {
    history.push(`/weapons/${weaponType.weaponTypeID}`);
  };

  return (
    <div>
      <h2>Select Weapon Type</h2>
      <div className="weapon-type-container">
        {weaponTypes.map((weaponType) => (
          <div key={weaponType.weaponTypeID} onClick={() => handleSelectWeaponType(weaponType)}>
            {weaponType.weaponTypeName}
          </div>
        ))}
      </div>
    </div>
  );
}

export default WeaponTypePage;
