import React, { useState } from 'react';
import ArmorTypePage from './ArmorTypePage';
import CaryllRunesPage from './CaryllRunesPage';
import HunterToolsPage from './HunterToolsPage';
import WeaponTypePage from './WeaponTypePage';
import './App.css';

function App() {
  // State to keep track of the active page
  const [activePage, setActivePage] = useState(null);

  // Function to handle page navigation
  const handlePageNavigation = (pageName) => {
    setActivePage(pageName);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Bloodborne Wiki</h1>
      </header>
      <div className="content">
        <div className="links">
          {/* Set onClick handlers to navigate to corresponding pages */}
          <button onClick={() => handlePageNavigation('armor')}>Armor</button>
          <button onClick={() => handlePageNavigation('caryllRunes')}>Caryll Runes</button>
          <button onClick={() => handlePageNavigation('hunterTools')}>Hunter Tools</button>
          <button onClick={() => handlePageNavigation('weapons')}>Weapons</button>
        </div>
        {/* Conditionally render the active page */}
        {activePage === 'armor' && <ArmorTypePage />}
        {activePage === 'caryllRunes' && <CaryllRunesPage />}
        {activePage === 'hunterTools' && <HunterToolsPage />}
        {activePage === 'weapons' && <WeaponTypePage />}
      </div>
    </div>
  );
}

export default App;
