import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ArmorTypePage() {
  const [armorTypes, setArmorTypes] = useState([]);
  const [activePage, setActivePage] = useState(null);

  useEffect(() => {
    // Fetch armor types data from API
    const fetchArmorTypes = async () => {
      try {
        const response = await axios.get('/api/armorTypes');
        setArmorTypes(response.data);
      } catch (error) {
        console.error('Error fetching armor types:', error);
      }
    };

    fetchArmorTypes();
  }, []);

  const handleSelectArmorType = (armorType) => {
    setActivePage(armorType);
    // Navigate to the selected armor type
    window.location.href = `/armor/${armorType.armorTypeID}`;
  };

  return (
    <div>
      <h2>Select Armor Type</h2>
      <div className="links">
        {/* Render each armor type as a clickable link */}
        {armorTypes.map((armorType) => (
          <a
            key={armorType.armorTypeID}
            className={activePage === armorType ? 'active' : ''}
            onClick={() => handleSelectArmorType(armorType)}
          >
            {armorType.armorTypeName}
          </a>
        ))}
      </div>
    </div>
  );
}

export default ArmorTypePage;
